#!/usr/bin/env python3
"""
Skrypt do tworzenia ikon dla LoL Jungle Assistant
Uruchom: python create_icons.py
"""

import base64
import os

# Dane base64 ikon
ICON_MAIN_B64 = ""

ICON_GRAY_B64 = ""

# UtwÃ³rz folder assets/icons jeÅ›li nie istnieje
os.makedirs("assets/icons", exist_ok=True)

# Dekoduj i zapisz ikony
def save_icon(base64_data, filename):
    data = base64.b64decode(base64_data)
    filepath = f"assets/icons/{filename}"
    with open(filepath, "wb") as f:
        f.write(data)
    print(f"âœ… Utworzono {filepath} ({len(data)} bajtÃ³w)")

# Zapisz ikony
save_icon(ICON_MAIN_B64, "icon.png")
save_icon(ICON_GRAY_B64, "icon-gray.png")

# Skopiuj gÅ‚Ã³wnÄ… ikonÄ™ jako launcher
save_icon(ICON_MAIN_B64, "launcher.png")

print("\nðŸŽ‰ Wszystkie ikony utworzone!")
print("Teraz moÅ¼esz uruchomiÄ‡: npm run build && npm start")